 ПОСЛЕ 15 ИЮНЯ', '', 'inherit', 'closed', 'closed', '', '513-revision-v1', '', '', '2020-06-06 21:01:28', '2020-06-06 18:01:28', '', 513, 'http://blessed-firm.com/2020/06/06/513-revision-v1/', 0, 'revision', '', 0);
