INSERT INTO `jobs` (`id`, `title`, `salary`, `category`, `city`, `ShortDescription`, `descriptiobn`, `image`, `slug`, `meta_description`, `meta_keywords`, `featured`, `created_at`, `updated_at`) VALUES
(5, 'przeprowadzki', '14', 'Magazyny', 'Błonie', 'Warehouse is looking for Physical worker', 'The warehouse needs men\n  2 movers\n  WAREHOUSE mainly deals with napkins and household goods\n  The work consists in unloading and loading completely different goods\n  Rate 14 PLN / hour, up to 26 years old - 15 PLN / hour\n  200-220 hours per month\n  We provide good housing near work - PLN 250 is withheld from wages', 'job\\March2021\\YQ1MYTl6ie8YRq7owDTd.jpg', 'gryz', 'Magazyn potrzebuje ludzi', 'Warehouse is looking for Physical worker', 1, '2021-03-17 10:30:00', '2021-03-25 21:55:25'),
(6, 'Operatorzy obróbki drewna CNC', '16', 'Magazyny', 'Elbląg', 'Wymaga to fabryk mebli w Elblągu\nMężczyźni poniżej 45 roku życia', 'Elbląskie fabryki mebli wymagają\n\n- Operatorzy maszyn CNC do drewna\n- Operatorzy maszyn do klejenia pvc\n\n15-19 zł za godzinę\n\n* pracę w nowoczesnym warsztacie\n* Stabilne zatrudnienie\nokoło 240-280 godzin pracy miesięcznie\n* 15-19 zł za godzinę (od ręki)\n\nKomfortowy pobyt\nUbezpieczenie ZUS\nPłace na czas\nPomoc koordynatora\nPozwolenie wojewódzkie, pomoc w załatwieniu dokumentów do karty pobytu\n\nWynagrodzenie zależy od doświadczenia zawodowego.\n\nPracuj na takich maszynach jak:\n\nFormatyzerko- okleiniarka dwustronna HOMAG, SCM, IMA, Wiertarka WECKEE BST 507, Wiertarka BIESSE TECHNO FDT\n\nWiertarka WECKEE BHX 500, BIESSE Skiper 105\n\nWiertarka WECKEE BHX 55, wiertarka WJ 23, MAGGI 321', 'job\\March2021\\TBjCqkjcAppbXLlnuFSr.png', 'cpy', 'Wiertarka WECKEE BHX 500, BIESSE Skiper 105\n\nWiertarka WECKEE BHX 55, wiertarka WJ 23, MAGGI 321 ', 'Wiertarka WECKEE BHX 500, BIESSE Skiper 105\n\nWiertarka WECKEE BHX 55, wiertarka WJ 23, MAGGI 321', 1, '2021-03-24 17:09:00', '2021-03-25 21:36:47'),
(7, 'Magazynier', '14', 'Magazyny', 'Błonie', 'Sprzedawca jest zobowiązany do pracy w magazynie serwetek pod Warszawą (Błonie).', 'Sprzedawca jest zobowiązany do pracy w magazynie serwetek pod Warszawą (Błonie).\n\nWymagania:\n\n- wiza gotowa lub bezwizowa\n\n- chęć do pracy\n\nWarunki pracy:\n\n- stawka 14 zł / godz\n\n- 10-godzinny dzień pracy, 200-240 godzin miesięcznie (zmiany dzienne)\n\n- mieszkanie jest zapewnione w pobliżu miejsca pracy (od wynagrodzenia odliczamy 250 zł)', 'job\\March2021\\hGjuu1Jrn5et6TUgqKMi.png', 'kad', 'wymagany sklepikarz ', 'wymagany sklepikarz ', 1, '2021-03-24 17:14:00', '2021-03-25 21:27:57'),
(8, 'Pracownik chłodni', '15', 'Magazyny', 'Otwock', 'Praca w chłodni lodów, warzyw, owoców, konserwacja', '- praca w chłodni lodów, warzyw, owoców, konserwacja\n- co godzinę przerwa 10 minut plus 1 półgodzinna przerwa obiadowa, przerwy płatne\n- ciepłą odzież dajemy gratis\n- mieszkanie jest w dobrych warunkach, od wynagrodzenia odliczamy 250 zł\nWymagania:\n- mężczyźni poniżej 55 roku życia\n- wiza pracownicza lub bezwizowa\n- gotowość do pracy w temperaturach ujemnych\n- znajomość języka polskiego, jak odbieranie zamówienia na fakturach', 'job\\March2021\\qpNyzVTaU8tq4j8KauHv.png', 'cold', 'Praca w chłodni lodów, warzyw, owoców, konserwacja ', 'Praca w chłodni lodów, warzyw, owoców, konserwacja ', 1, '2021-03-24 17:22:00', '2021-03-25 20:18:40'),
(9, 'Pracownik kuchni', '14', 'kuchnia', 'Krakow', 'Prace przy formowaniu i pakowaniu pierogów i knedli, są też naleśniki, placki ziemniaczane Cena 14 zł / h', 'Pracuj przy modelowaniu i pakowaniu pierogów i pierogów, są też naleśniki, naleśniki\nStawka 13 zł / h (do 26 lat 14 zł / h), w przypadku zmiany nocnej stawka 14 zł / h\nZmyvak - 13,5 zł / godz\nZakwaterowanie jest zapewnione na dobrych warunkach, potrącając 200 zł z wynagrodzenia\nWymagana jest czysta książka (jeśli nie masz książki odliczamy 100 zł od pierwszej pensji) oraz minimalna znajomość języka polskiego wymagana jest do komunikacji z Kerovnikami w produkcji\nWięcej informacji telefonicznie\n\nDolny przycisk', 'job\\March2021\\10bnNzZebhKUGO9XdUbE.jpg', 'cook', 'krakow', 'krakow', 1, '2021-03-24 17:31:00', '2021-03-25 20:10:07'),
(10, 'Pojemnik zbierający', '15', 'Budowa', 'Łódź', 'Praca dla mężczyzn przy montażu kontenerów mieszkalnych na przedmieściach Łodzi (Stara Gadka, Rzgów)', 'Praca dla mężczyzn przy montażu kontenerów mieszkalnych na przedmieściach Łodzi (Stara Gadka, Rzgów)\n  Zmiany w południe, od poniedziałku do piątku / soboty\n  Praca nie jest trudna, montaż pojemników z aluminiowych płyt\n  Doświadczenie zawodowe nie jest wymagane, fizycznie nie ciężka praca dla mężczyzn poniżej 50 roku życia\n  Zapewniamy bezpłatne zakwaterowanie', 'job\\March2021\\I0LpA3wozeIcAraNorFR.png', 'kont', 'Praca nie jest trudna ', 'Praca nie jest trudna ', 1, '2021-03-24 17:52:00', '2021-03-25 20:00:09'),
(11, 'Międzynarodowy kierowca', '5000', 'Kierowca', 'Warszawa', 'Wylot z Warszawy 15.01.2021 (minimum na miesiąc)\nWymagana jest osoba posiadająca wizę (min. 3 miesiące przed końcem wizy)', 'Wymagany międzynarodowy kierowca\n\n3,5 tony\n\nWynagrodzenie 4-6 tys. (W zależności od km)\n\nWylot z Warszawy 15.01.2021 (minimum na miesiąc)\nWymagana jest osoba posiadająca wizę (min. 3 miesiące przed końcem wizy)', 'job\\March2021\\DXKrAtpMlabCoS8Oqdz7.jpg', 'drive', '\nWycieczki do krajów Beneluksu + Niemcy', 'Поездки по странам Бенилюкса+Германия', 1, '2021-03-24 18:17:00', '2021-03-25 21:53:34');
